import "./App.css";
import { useState, useEffect } from "react";
import BookShelf from "./BookShelf";
import * as BooksAPI from "./BooksAPI"
import BookSearch from "./BookSearch";
import { Routes, Route, Link} from "react-router-dom";

const App = () => {

  const [books, setBooks] = useState([]);
  
  useEffect(()=>{
    const getBooks = async () => {
      const res = await BooksAPI.getAll();
      setBooks(res);
    }

    getBooks();
  }, [])

  const currentlyReadingBooks = books.filter((c) => c.shelf == "currentlyReading")
  const wantToReadBooks = books.filter((c) => c.shelf == "wantToRead")
  const readBooks = books.filter((c) => c.shelf === "read")



  return (
    <div className="app">
      
        <div className="list-books">
          <div className="list-books-title">
            <h1>MyReads</h1>
          </div>
          <div className="list-books-content">
            <div>
            <BookShelf books={currentlyReadingBooks} shelfTitle={"Currently Reading"} />
            <BookShelf books={wantToReadBooks} shelfTitle={"Want to Read"} />
            <BookShelf books={readBooks} shelfTitle={"Read"} />
            {<Routes>
              <Route 
              path="/search" 
              element={
                <BookSearch />
              } />
            </Routes>}
              
            </div>
          </div>
          <div className="open-search">
          {/*<Link to="/search">
                Add a book
          </Link>*/}
          </div>
        </div>
     
    </div>
  );
}

export default App;
