import PropTypes from "prop-types";
import BookShelf from "./BookShelf";
import {Link} from "react-router-dom";

const ListBooks = ({books}) => {

  const currentlyReadingBooks = books.filter((c) => c.shelf == "currentlyReading")
  const wantToReadBooks = books.filter((c) => c.shelf == "wantToRead")
  const readBooks = books.filter((c) => c.shelf === "read")

    return (
        <div>
            <BookShelf books={currentlyReadingBooks} shelfTitle={"Currently Reading"} />
            <BookShelf books={wantToReadBooks} shelfTitle={"Want to Read"} />
            <BookShelf books={readBooks} shelfTitle={"Read"} />
            <div className="open-search">
                <Link to="/search">
                        Add a book
                </Link>
            </div>
        </div>
    );
};





ListBooks.propTypes={
books: PropTypes.array.isRequired
}

export default ListBooks;
