  const [showSearchPage] = useState(false);

  return (
    <div className="app">
      
        <div className="list-books">
          <div className="list-books-title">
            <h1>MyReads</h1>
          </div>
          <div className="list-books-content">
            <div>
            <BookShelf />
            {/*<Routes>
              <Route 
              exact 
              path="/create" 
              element={
                <BookSearch />
              } />
            </Routes>*/}
           
              
            </div>
          </div>
          <div className="open-search">
          <Link >
                Add a book
          </Link>
          </div>
        </div>
     
    </div>
  );
}

export default App;
